var new_scroll_position=0;var last_scroll_position;var header=document.getElementsByClassName("header")[0];window.addEventListener("scroll",function(e){last_scroll_position=window.scrollY;if(new_scroll_position<last_scroll_position&&last_scroll_position>80){document.getElementById("left-col").classList.remove("show-sidebar");header.classList.remove("slideDown");header.classList.add("slideUp")}if(new_scroll_position>last_scroll_position){header.classList.remove("slideUp");header.classList.add("slideDown")}new_scroll_position=last_scroll_position});function toggleSidebar(){var show_sidebar=document.getElementById("left-col").classList.contains("show-sidebar");if(show_sidebar==false){document.getElementById("left-col").classList.add("show-sidebar")}else{document.getElementById("left-col").classList.remove("show-sidebar")}}var menu=document.getElementById("menu");menu.onclick=toggleSidebar;